import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import TextField from '@material-ui/core/TextField';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import InputAdornment from '@material-ui/core/InputAdornment';
import Grid from '@material-ui/core/Grid';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    useHistory,
    Link
} from "react-router-dom";
import IconButton from "@material-ui/core/IconButton";
import './Signup.css'
import * as Styles from './SignupStyles.js'
import {Search} from "../../SearchPage/Search/Search";


const useStyles = makeStyles((theme) => ({
    paper: {
        display: 'block',
        margin: '5% auto',
        width: 450,
        backgroundColor: theme.palette.background.paper,
        borderRadius: '10px',
        maxHeight: 400,
        overflow: 'auto',
        padding: theme.spacing(2, 1, 3),
        outline: 'none',
        '&::-webkit-scrollbar': {
            width: 15,
        },
        '&::-webkit-scrollbar-track': {
            borderRadius: 30,
        },

        '&::-webkit-scrollbar-thumb': {
            backgroundColor: 'rgb(255, 137, 137)',
            borderRadius: 30,
        },
        /* Handle */
        '&::-webkit-scrollbar-thumb:hover': {
            background: 'rgb(247, 92, 92)',
        }
    },
}));

export default function Login(props) {
    const history = useHistory();
    const classes = useStyles();
    const [values, setValues] = React.useState({
        amount: '',
        password: '',
        weight: '',
        weightRange: '',
        showPassword: false,
    });


    const handleClose = () => {
        history.goBack();
    };


    const handleClickShowPassword = () => {
        setValues({...values, showPassword: !values.showPassword});
    };

    const handleMouseDownPassword = (event) => {
        event.preventDefault();
    };

    const body = (
        <div className={classes.paper}>
            <Styles.FormControlSignup>
                <header className="SignupTitle">
                    Signup
                </header>
                <Grid item>
                    <TextField
                        id="firstName"
                        placeholder="First Name"
                        variant="outlined"/>
                    <TextField
                        id="lastName"
                        placeholder="Last Name"
                        variant="outlined"/>
                </Grid>
                <TextField
                    id="Email address"
                    placeholder="Email address"
                    variant="outlined"/>

                <TextField
                    id="username"
                    placeholder="Username"
                    variant="outlined"/>

                <TextField
                    id="password"
                    type={values.showPassword ? 'text' : 'password'}
                    placeholder="Password"
                    variant="outlined"
                    filled="true"
                    InputProps={{
                        endAdornment: (
                            <InputAdornment position="end">
                                <IconButton
                                    aria-label="toggle password visibility"
                                    onClick={handleClickShowPassword}
                                    onMouseDown={handleMouseDownPassword}
                                >
                                    {values.showPassword ? <Visibility/> : <VisibilityOff/>}
                                </IconButton>
                            </InputAdornment>
                        )
                    }}/>

                <TextField
                    id="confirmPassword"
                    type="password"
                    placeholder="Confirm Password"
                    variant="outlined"
                />
                <Link to="/search" style={{textDecoration: 'none'}}>

                    <Styles.SubmitButtonStyled>
                        Submit
                    </Styles.SubmitButtonStyled>
                </Link>
            </Styles.FormControlSignup>
        </div>
    );

    return (
        <div>
            <Modal
                open={true}
                onClose={handleClose}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                border={0}
            >
                {body}
            </Modal>
        </div>
    );
}
